var input = require('prompt-sync')();

var php , c , csh , python , js , total , avg;

console.log("====== Input your score =====")

php = input("Enter PHP score : ");
c = input("Enter C score : ");
csh = input("Enter C# score : ");
python = input("Enter Python score : ");
js = input("Enter JS score : ");

// condition

total = php + c + csh + python + js;

avg = total / 5;

console.log("===== Result =====")

console.log("PHP : " + php);
console.log("C : " + c);
console.log("C# : " + csh);
console.log("Python : " + python);
console.log("JS : " + js);

if (avg >= 90) {
    console.log("You got A");
} else if(avg <90 && avg >=80 ) {
    console.log("You got B");
} else if (avg < 80 && avg >=70){
    console.log("You got C")
} else if (avg <70 && avg >= 60){
    console.log("You got D")
} else if (avg < 60 && avg >=50) {
    console.log("You got E ")
} else {
    console.log("You got F")
}
