var input = require('prompt-sync')();

var id,name,sex,salary,position,totalSalary;

console.log("========== Insert Info ===========");

id = input("Enter ID : ");
name = input("Enter Name : ");
sex = input("Enter Sex : ");
salary = parseFloat(input("Enter Salary : "));
position = input("Enter Position : ");

// If else condition

if (position == "Manager") {
    salary +=  salary * 0.2;
} else if (position == "Accounting") {
    salary += salary * 0.15;
} else if (position == "IT") {
    salary += salary * 0.25;
} else {
    salary += salary * 0.1;
}

console.log("========== Display Info ==========");

console.log("Name : " + name);
console.log("Sex : " + sex);
console.log("salary : " + salary);
console.log("Position : " + position);