// get date

let date = new Date();

console.log("Day : " + date.getDate() + " th"); // current day of the month (16 th)

// getFullYear

console.log("Year : " + date.getFullYear()); // current year

// getTime

console.log("Milisecond : " + date.getTime());

// getMonth

console.log("Month : " + date.getMonth() + 1); // default Jan = 0 , So we plus 1  

// getHour

console.log("Hour : " + date.getHours()); // mong 11

// getMinute

console.log("Minutes : " + date.getMinutes()); // 0 minute

// getSeconds

console.log("Second : " + date.getSeconds()); // 28 second
