// replace(searchValue,newValue)

console.log(myName.replace("Somonor","Jakarusik")); // change Somonor to Jakarusik | result : Jakarusik

// slice(start,end)

console.log(myName.slice(0,3)); // show index 0 -> 1 | reault : Som

