var input = require('prompt-sync')();

var sc1 , sc2 , sc3 , sc4 , sc5 , total , avg , grade;

sc1 = parseInt(input("Insert Score1 : "));
sc2 = parseInt(input("Insert Score2 : "));
sc3 = parseInt(input("Insert Score3 : "));
sc4 = parseInt(input("Insert Score4 : "));
sc5 = parseInt(input("Insert Score5 : "));

total = sc1 + sc2 + sc3 + sc4 + sc5;

avg = total/5;

// ternary condition

grade = (avg > 90 && avg <= 100)? 'A':
(avg > 80 && avg <= 90)? 'B':
(avg > 70 && avg <= 80)? 'C':
(avg > 60 && avg <= 70)? 'D':
(avg > 50 && avg <= 60)? 'E': 'F' ;

console.log("Score 1 : " + sc1);
console.log('Score 2 : ' + sc2);
console.log("Score 3 : " + sc3);
console.log("Score 4 : " + sc4);
console.log("Score 5 : " + sc5);
console.log("Grade : " + grade);